import { TestBed } from '@angular/core/testing';
import { HttpClientTestingModule, HttpTestingController } from '@angular/common/http/testing';
import { MatchService } from './match.service';
import { environment } from '../../../environments/environment';
import { Match, CreateMatchRequest } from '../models/match.model';

describe('MatchService', () => {
  let service: MatchService;
  let httpMock: HttpTestingController;
  const apiUrl = `${environment.apiUrl}/api/match`;

  // Test data
  const mockMatch: Match = {
    idMatch: 1,
    idCoach: 1,
    idField: 1,
    idDate: 1,
    idCategory: 1,
    idTeamA: 1,
    idTeamB: 2
  };

  const mockMatches: Match[] = [mockMatch];

  const mockCreateMatchRequest: CreateMatchRequest = {
    idField: 1,
    idConfirmDate: 1,
    idCategory: 1,
    idPlayersTeamA: [1, 2, 3],
    idPlayersTeamB: [4, 5, 6]
  };

  beforeEach(() => {
    TestBed.configureTestingModule({
      imports: [HttpClientTestingModule],
      providers: [MatchService]
    });

    service = TestBed.inject(MatchService);
    httpMock = TestBed.inject(HttpTestingController);
  });

  afterEach(() => {
    httpMock.verify(); // Verifies that no requests are outstanding
  });

  it('should be created', () => {
    expect(service).toBeTruthy();
  });

  describe('createMatch', () => {
    it('should create a match successfully', () => {
      const idCoach = 1;
      const mockResponse = { message: 'Partido creado exitosamente', matchId: 1 };

      service.createMatch(idCoach, mockCreateMatchRequest).subscribe(response => {
        expect(response).toEqual(mockResponse);
      });

      const req = httpMock.expectOne(`${apiUrl}/coaches/${idCoach}/createMatch`);
      expect(req.request.method).toBe('POST');
      expect(req.request.body).toEqual(mockCreateMatchRequest);
      req.flush(mockResponse);
    });

    it('should handle create match error', () => {
      const idCoach = 1;
      const errorMessage = 'Error al crear el partido';

      service.createMatch(idCoach, mockCreateMatchRequest).subscribe({
        error: (error) => {
          expect(error.status).toBe(400);
          expect(error.error).toBe(errorMessage);
        }
      });

      const req = httpMock.expectOne(`${apiUrl}/coaches/${idCoach}/createMatch`);
      req.flush(errorMessage, { status: 400, statusText: 'Bad Request' });
    });
  });

  describe('getAllMatches', () => {
    it('should get all matches successfully', () => {
      service.getAllMatches().subscribe(matches => {
        expect(matches).toEqual(mockMatches);
      });

      const req = httpMock.expectOne(`${apiUrl}`);
      expect(req.request.method).toBe('GET');
      req.flush(mockMatches);
    });

    it('should handle get matches error', () => {
      const errorMessage = 'Error al obtener partidos';

      service.getAllMatches().subscribe({
        error: (error) => {
          expect(error.status).toBe(500);
          expect(error.error).toBe(errorMessage);
        }
      });

      const req = httpMock.expectOne(`${apiUrl}`);
      req.flush(errorMessage, { status: 500, statusText: 'Internal Server Error' });
    });
  });

  describe('getMatchById', () => {
    it('should get a match by id successfully', () => {
      const matchId = 1;

      service.getMatchById(matchId).subscribe(match => {
        expect(match).toEqual(mockMatch);
      });

      const req = httpMock.expectOne(`${apiUrl}/${matchId}`);
      expect(req.request.method).toBe('GET');
      req.flush(mockMatch);
    });

    it('should handle get match by id error', () => {
      const matchId = 1;
      const errorMessage = 'Partido no encontrado';

      service.getMatchById(matchId).subscribe({
        error: (error) => {
          expect(error.status).toBe(404);
          expect(error.error).toBe(errorMessage);
        }
      });

      const req = httpMock.expectOne(`${apiUrl}/${matchId}`);
      req.flush(errorMessage, { status: 404, statusText: 'Not Found' });
    });
  });

  describe('getMatchesByCategory', () => {
    it('should get matches by category successfully', () => {
      const categoryId = 1;

      service.getMatchesByCategory(categoryId).subscribe(matches => {
        expect(matches).toEqual(mockMatches);
      });

      const req = httpMock.expectOne(`${apiUrl}/category/${categoryId}`);
      expect(req.request.method).toBe('GET');
      req.flush(mockMatches);
    });

    it('should handle get matches by category error', () => {
      const categoryId = 1;
      const errorMessage = 'Error al obtener partidos de la categoría';

      service.getMatchesByCategory(categoryId).subscribe({
        error: (error) => {
          expect(error.status).toBe(500);
          expect(error.error).toBe(errorMessage);
        }
      });

      const req = httpMock.expectOne(`${apiUrl}/category/${categoryId}`);
      req.flush(errorMessage, { status: 500, statusText: 'Internal Server Error' });
    });
  });

  describe('getMatchesByCoach', () => {
    it('should get matches by coach successfully', () => {
      const coachId = 1;

      service.getMatchesByCoach(coachId).subscribe(matches => {
        expect(matches).toEqual(mockMatches);
      });

      const req = httpMock.expectOne(`${apiUrl}/coaches/${coachId}`);
      expect(req.request.method).toBe('GET');
      req.flush(mockMatches);
    });

    it('should handle get matches by coach error', () => {
      const coachId = 1;
      const errorMessage = 'Error al obtener partidos del coach';

      service.getMatchesByCoach(coachId).subscribe({
        error: (error) => {
          expect(error.status).toBe(500);
          expect(error.error).toBe(errorMessage);
        }
      });

      const req = httpMock.expectOne(`${apiUrl}/coaches/${coachId}`);
      req.flush(errorMessage, { status: 500, statusText: 'Internal Server Error' });
    });
  });

  describe('getMatchesByPlayer', () => {
    it('should get matches by player successfully', () => {
      const playerId = 1;

      service.getMatchesByPlayer(playerId).subscribe(matches => {
        expect(matches).toEqual(mockMatches);
      });

      const req = httpMock.expectOne(`${apiUrl}/players/${playerId}`);
      expect(req.request.method).toBe('GET');
      req.flush(mockMatches);
    });

    it('should handle get matches by player error', () => {
      const playerId = 1;
      const errorMessage = 'Error al obtener partidos del jugador';

      service.getMatchesByPlayer(playerId).subscribe({
        error: (error) => {
          expect(error.status).toBe(500);
          expect(error.error).toBe(errorMessage);
        }
      });

      const req = httpMock.expectOne(`${apiUrl}/players/${playerId}`);
      req.flush(errorMessage, { status: 500, statusText: 'Internal Server Error' });
    });
  });

  describe('deleteMatch', () => {
    it('should delete a match successfully', () => {
      const matchId = 1;
      const successMessage = 'Partido eliminado exitosamente';

      service.deleteMatch(matchId).subscribe(response => {
        expect(response).toBe(successMessage);
      });

      const req = httpMock.expectOne(`${apiUrl}/${matchId}`);
      expect(req.request.method).toBe('DELETE');
      req.flush(successMessage);
    });

    it('should handle delete match error', () => {
      const matchId = 1;
      const errorMessage = 'Partido no encontrado';

      service.deleteMatch(matchId).subscribe({
        error: (error) => {
          expect(error.status).toBe(404);
          expect(error.error).toBe(errorMessage);
        }
      });

      const req = httpMock.expectOne(`${apiUrl}/${matchId}`);
      req.flush(errorMessage, { status: 404, statusText: 'Not Found' });
    });
  });
});
