import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { environment } from '../../environments/environment';
import { ConfirmDate, ConfirmDateRequest, ConfirmDateResponse } from '../models/confirm-date.model';

@Injectable({
  providedIn: 'root'
})
export class ConfirmDateService {
  private apiUrl = `${environment.apiUrl}/api/confirmdate`;

  constructor(private http: HttpClient) {}

  // Obtener todas las fechas
  getAllDates(): Observable<ConfirmDate[]> {
    return this.http.get<ConfirmDate[]>(`${this.apiUrl}`);
  }

  // Obtener fecha por ID
  getDateById(idDate: number): Observable<ConfirmDate> {
    return this.http.get<ConfirmDate>(`${this.apiUrl}/${idDate}`);
  }

  // Eliminar una fecha
  deleteDate(idDate: number): Observable<string> {
    return this.http.delete<string>(`${this.apiUrl}/${idDate}`);
  }

  // Confirmar un jugador para una fecha
  confirmPlayer(idDate: number, idPlayer: number): Observable<string> {
    return this.http.post<string>(`${this.apiUrl}/${idDate}/confirm/${idPlayer}`, {});
  }

  // Obtener jugadores confirmados para una fecha
  getConfirmedPlayers(idDate: number): Observable<number[]> {
    return this.http.get<number[]>(`${this.apiUrl}/${idDate}/players`);
  }
}
